createToken = function (customerNumber, customerUsername, customerPassword) {
    let environment = pm.environment.get("env");
    if (environment === "local") {
        environment = "dev";
    }
    console.log("CHECKING TOKEN");
    pm.sendRequest({
        url: "https://security-internet.totta." + environment + ".corp/oauth/check_token",
        method: "POST",
        header: {
            "User-Agent": "PostmanRuntime/7.17.1",
            "Content-Type": "application/x-www-form-urlencoded"
        },
        body: {
            mode: 'urlencoded',
            urlencoded: [{
                    key: "token",
                    value: pm.globals.get("accessToken"),
                    disabled: false
                }
            ]
        }
    },
        function (err, res) {
        if (res.json().user_name == customerUsername) {
            console.log("TOKEN ALREADY CREATED");
        } else {
            console.log("CREATING NEW TOKEN");
            const role = pm.variables.get("role") ? pm.variables.get("role") : "Web";
            const urlencoded = [{
                key: "client_id",
                value: pm.environment.get("tokenClientId"),
                disabled: false
            }, {
                key: "grant_type",
                value: "password",
                disabled: false
            }, {
                key: "username",
                value: customerUsername,
                disabled: false
            }, {
                key: "password",
                value: customerPassword,
                disabled: false
            }, {
                key: "role",
                value: role,
                disabled: false
            }, {
                key: "captcha",
                value: true,
                disabled: false
            }, {
                key: "channel",
                value: "WEB",
                disabled: false
            }, {
                key: "fraudData",
                value: getFraudData(false, customerNumber),
                disabled: false
            }];
            if(pm.environment.get("tokenClientSecret") != null 
                && pm.environment.get("tokenClientSecret") != undefined 
                && pm.environment.get("tokenClientSecret") != "") {
                    urlencoded.push({
                        key: "client_secret",
                        value: pm.environment.get("tokenClientSecret"),
                        disabled: false
                    });
            }
            pm.sendRequest({
                url: "https://security-internet.totta." + environment + ".corp/oauth/token",
                method: "POST",
                header: {
                    "User-Agent": "PostmanRuntime/7.17.1",
                    "Content-Type": "application/x-www-form-urlencoded"
                },
                body: {
                    mode: 'urlencoded',
                    urlencoded: urlencoded
                }
            },
                function (err, res) {
                if (res.json().error == "fraud_sca") {
                    console.log("SECOND FACTOR AUTHENTICATION");
                    const urlencoded = [{
                        key: "client_id",
                        value: pm.environment.get("tokenClientId"),
                        disabled: false
                    }, {
                        key: "grant_type",
                        value: "multi_factor",
                        disabled: false
                    }, {
                        key: "mfa_code",
                        value: res.json().additional_info.mfa_code,
                        disabled: false
                    }, {
                        key: "fraudData",
                        value: getFraudData(true, customerNumber),
                        disabled: false
                    }];
                    if(pm.environment.get("tokenClientSecret") != null 
                    && pm.environment.get("tokenClientSecret") != undefined 
                    && pm.environment.get("tokenClientSecret") != "") {
                        urlencoded.push({
                            key: "client_secret",
                            value: pm.environment.get("tokenClientSecret"),
                            disabled: false
                        });
                }
                    pm.sendRequest({
                        url: "https://security-internet.totta." + environment + ".corp/oauth/token",
                        method: "POST",
                        header: {
                            "User-Agent": "PostmanRuntime/7.17.1",
                            "Content-Type": "application/x-www-form-urlencoded"
                        },
                        body: {
                            mode: 'urlencoded',
                            urlencoded: urlencoded
                        }
                    },
                        function (err, response) {
                        pm.globals.set("accessToken", response.json().access_token);
                    });
                } else {
                    pm.globals.set("accessToken", res.json().access_token);
                }
            });
        }
    });
},
