createTokenEstruturais = function (user, password, clientId) {
    if (!clientId) {
        clientId = pm.environment.get("tokenClientId");
    }
    let environment = pm.environment.get("env");
    if (environment === "local") {
        environment = "dev";
    }
    pm.sendRequest({
        url: "http://estructurais.totta.pre.corp/SSOTottaTokenAS/ws/SECMR_Def_Listener/ws/SECMR_Def_Listener",
        method: "POST",
        header: {
            'Content-Type': 'text/xml',
            'SOAPAction': '""'
        },
        body: {
            mode: 'raw',
            raw: "<soapenv:Envelope xmlns:soapenv=\"http://schemas.xmlsoap.org/soap/envelope/\" xmlns:v1=\"http://www.isban.es/webservices/TECHNICAL_FACADES/Security/F_facseg_security/intranet/ACFACSEGSecurity/v1\"><soapenv:Header> <wsse:Security soapenv:mustUnderstand=\"1\" xmlns:wsse=\"http://docs.oasis-open.org/wss/2004/01/oasis-200401-wss-wssecurity-secext-1.0.xsd\"><wsse:UsernameToken><wsse:Username>" + user + "</wsse:Username><wsse:Password Type=\"http://docs.oasis-open.org/wss/2004/01/oasis-200401-wss-username-token-profile-1.0#PasswordText\">" + password + "</wsse:Password></wsse:UsernameToken></wsse:Security></soapenv:Header><soapenv:Body><v1:authenticateCredential facade=\"ACFACSEGSecurity\"><CB_AuthenticationData><!--Optional:--><user>" + user + "</user><!--Optional:--><password>" + password + "</password></CB_AuthenticationData><userAddress>127.0.0.1</userAddress></v1:authenticateCredential></soapenv:Body></soapenv:Envelope>"
        }
    },
        function (err, res) {
        function arrayBufferToString(buffer) {
            var arr = new Uint8Array(buffer);
            var str = String.fromCharCode.apply(String, arr);
            return str;
        }
        var jsonResponse = xml2Json(arrayBufferToString(res.stream));
        var token = jsonResponse['soap-env:Envelope']['soap-env:Body']['prefixRigel0:authenticateCredentialResponse'].methodResult.tokenCredential;
        pm.globals.set("accessToken", token);
        pm.sendRequest({
            url: "https://security-service.totta." + environment + ".corp/oauth/token",
            method: "POST",
            header: {
                "User-Agent": "PostmanRuntime/7.17.1",
